  ngOnInit(): void {
    this.leerValoresUrl();
    this.buscarPeliculas(this.form.value as filtroPelicula);
    this.form.valueChanges.subscribe((valores) => {
      this.peliculas = this.peliculasOriginal;
      this.buscarPeliculas(valores as filtroPelicula);
      this.ecribirParametrosBusquedaUrl(valores as filtroPelicula);
    });
  }

  buscarPeliculas(valores: filtroPelicula) {
    if (valores.titulo) {
      this.peliculas = this.peliculas.filter(
        (pelicula) => pelicula.titulo.indexOf(valores.titulo!) !== -1
      );
    }

    if (valores.generoId !== 0) {
      this.peliculas = this.peliculas.filter(
        (pelicula) => pelicula.generos.indexOf(valores.generoId) !== -1
      );
    }

    if (valores.proximosEstrenos) {
      this.peliculas = this.peliculas.filter((pelicula) => pelicula.proximoEstreno);
    }
    if (valores.enCines) {
      this.peliculas = this.peliculas.filter((pelicula) => pelicula.enCines);
    }
  }

  ecribirParametrosBusquedaUrl(valores: filtroPelicula) {
    let queryStrings = [];

    if (valores.titulo) {
      queryStrings.push(`titulo=${encodeURIComponent(valores.titulo)}`);
    }
    if (valores.generoId !== 0) {
      queryStrings.push(`generoId=${valores.generoId}`);
    }
    if (valores.proximosEstrenos) {
      queryStrings.push(`proximosEstrenos=${valores.proximosEstrenos}`);
    }
    if (valores.enCines) {
      queryStrings.push(`proximosEstrenos=${valores.enCines}`);
    }
    this.location.replaceState('peliculas/filtrar', queryStrings.join('&'));
  }

  leerValoresUrl(){
    this.activateRoute.queryParams.subscribe((params) => {
      let objeto: any = {};
      if (params['titulo']) {
        objeto.titulo = params['titulo'];
      }
      if (params['generoId']) {
        objeto.generoId = Number(params['generoId']);
      }
      if (params['proximosEstrenos']) {
        objeto.proximosEstrenos = params['proximosEstrenos'];
      }
      if (params['enCines']) {
        objeto.enCines = params['enCines'];
      }
      this.form.patchValue(objeto);
    });
  }

  limpiar() {
    this.form.reset({ titulo: '', generoId: 0, proximosEstrenos: false, enCines: false })
  }

